	else {
		this.refresh();
	}

	if (this.eventHandlers['itemtypechange'] && this.eventHandlers['itemtypechange'].length) {
		var self = this;
		this.eventHandlers['itemtypechange'].forEach(function (f) f.bind(self)());
	}

	return true;
}

